package J1120;

public class J1120_06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.printf("Hello,%s\n","Java");
		System.out.printf("2>1的結果是%b\n",2>1);
		System.out.printf("%d\n",78); //十進位整數
		System.out.printf("%o\n",78); //八進位整數
		System.out.printf("%x\n",78); //16進位整數
		System.out.printf("%+d,%+d\n",1234,-1234);
		System.out.printf("%.2f\n",123.566);
		System.out.printf("%8.2f\n",123.566);
	}

}
